def min_operations(A, B):
    N = len(A)
    pieces = [i for i in range(N) if A[i] == '1']
    goal = [i for i in range(N) if B[i] == '1']

    if len(pieces) != len(goal):
        return -1

    operations = 0
    for i in range(N):
        if pieces[i] != goal[i]:
            operations += abs(pieces[i] - goal[i])

    return operations

