def reverse_substring(s, l, r):
    """Reverses the substring of s from the l-th character to the r-th character."""
    s = list(s)
    for i in range((r - l + 1) // 2):
        s[l + i], s[r - i - 1] = s[r - i - 1], s[l + i]
    return ''.join(s)

def count_distinct_strings(N, S):
    """Counts the number of distinct strings that can be formed by reversing substrings of S."""
    if N == 0:
        return 1

    # Initialize the dp array
    dp = [0] * (N + 1)
    dp[0] = 1

    # Iterate over the string length
    for i in range(1, N + 1):
        # Check if the substring from the start to i is a valid parenthesis sequence
        if is_valid_parenthesis(S[:i]):
            dp[i] = dp[i - 1]

        # Check if the substring from the start to i is a valid parenthesis sequence and can be reversed
        if is_valid_parenthesis(S[:i]) and is_valid_parenthesis(reverse_substring(S, 0, i - 1)):
            dp[i] = (dp[i] + dp[i - 1]) % 998244353

    return dp[N]

def is_valid_parenthesis(s):
    """Checks if the string is a valid parenthesis sequence."""
    stack = []
    for char in s:
        if char == '(':
            stack.append(char)
        elif char == ')':
            if not stack or stack.pop() != '(':
                return False
    return not stack

