def reverse_substring(s, l, r):
    """Reverse the substring of s from index l to r."""
    return s[:l] + s[l:r+1][::-1] + s[r+1:]

def count_distinct_strings(N, S):
    """Count the number of distinct strings that can be obtained by reversing substrings."""
    # Base case: if the string is empty, return 1
    if N == 0:
        return 1

    # Base case: if the string is a valid parenthesis sequence, return 2
    if is_valid_parenthesis(S):
        return 2

    # Initialize the count of distinct strings
    count = 0

    # Try reversing all possible substrings
    for i in range(N):
        for j in range(i, N):
            if is_valid_parenthesis(reverse_substring(S, i, j)):
                count += count_distinct_strings(N - (j - i + 1), reverse_substring(S, i, j))

    return count % 998244353

def is_valid_parenthesis(s):
    """Check if the string is a valid parenthesis sequence."""
    balance = 0
    for char in s:
        if char == '(':
            balance += 1
        else:
            balance -= 1
        if balance < 0:
            return False
    return balance == 0

