def is_valid_parenthesis(s):
    """Checks if the string is a valid parenthesis sequence."""
    stack = []
    for char in s:
        if char == '(':
            stack.append(char)
        elif char == ')':
            if not stack or stack.pop() != '(':
                return False
    return not stack

