    """Count the number of distinct strings that can be formed by reversing substrings of s."""
    from collections import defaultdict
    from functools import lru_cache

    @lru_cache(None)
    def dp(i, j):
        """Return the number of distinct strings that can be formed by reversing substrings of s[i:j+1]."""
        if i == j:
            return 1
        if i > j:
            return 1
        if s[i] == s[j]:
            return dp(i + 1, j - 1)
        return dp(i + 1, j) + dp(i, j - 1)

    return dp(0, n - 1) % 998244353

