from collections import defaultdict

def read_input():
    return map(int, sys.stdin.read().split())

def solve(R, B):
    if R + B < 2:
        return "No"

    # Initialize the board
    board = defaultdict(lambda: defaultdict(lambda: None))

    # Place the first piece
    if R > 0:
        board[1][1] = 'R'
        R -= 1
    else:
        board[1][1] = 'B'
        B -= 1

    # Place the remaining pieces
    for _ in range(R + B - 1):
        if R > 0:
            board[1][1] = 'R'
            R -= 1
        else:
            board[1][1] = 'B'
            B -= 1

    # Check if the board is valid
    if not is_valid(board):
        return "No"

    # Return the placement
    return "Yes\n" + "\n".join(f"{board[r][c]}" for r in range(1, 10**9 + 1) for c in range(1, 10**9 + 1))

def is_valid(board):
    # Check if there are more than one piece on the same square
    for r in range(1, 10**9 + 1):
        for c in range(1, 10**9 + 1):
            if board[r][c] is not None and board[r][c] != board[r][c-1]:
