import sys
from collections import defaultdict
from math import factorial
from functools import reduce

input = sys.stdin.read
data = input().split()

N = int(data[0])
Q = int(data[1])

A = [int(data[i]) for i in range(2, N + 2)]
u = [int(data[i]) for i in range(N + 2, N + 2 + Q)]
v = [int(data[i]) for i in range(N + 2 + Q, N + 2 + 2 * Q)]

def tree_distance(u, v, A, P):
    if u == v:
        return 0
    if P[u] == v:
        return A[u - 1]
    if P[u] == 0:
        return 0
    return A[u - 1] + tree_distance(P[u], v, A, P)

def tree_distance_sum(A, P):
    return sum(tree_distance(u, v, A, P) for u, v in zip(u, v))

def tree_distance_sum_all(A, P):
    return sum(tree_distance_sum(A, P) for P in permutations(range(1, N + 1)))

def permutations(iterable, r=None):
    # permutations('ABCD', 2) --> AB AC AD BA BC BD CA CB CD
    # permutations(range(3)) --> 012 021 102 120 201 210
    pool = tuple(iterable)
    n = len(pool)
    if r is None:
        r = n
    if r > n:
        return
    indices = list(range(n))
    cycles = list(range(n))
    yield tuple(pool[i] for i in indices[:r])
    while True:
        i = 0
        while i < n:
            k = cycles[i]
            if k < i:
                break
            i += 1
        else:
            return
        j = (i + 1) % n
        while j != i:
            k = cycles[j]
            if k < i:
                break
            j = (j + 1) % n
        else:
            cycles[i], cycles[j] = cycles[j], cycles[i]
            yield tuple(pool[i] for i in indices[:r])
            continue
        k = (k + 1) % n
        while k != i:
            cycles[k], cycles[j] = cycles[j], cycles[k]
            yield tuple(pool[i] for i in indices[:r])
