    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        Finds the length of the longest special path in a tree and the minimum number of nodes in all possible longest special paths.

        :param edges: List of edges representing the tree, where each edge is a list [u, v, length].
        :param nums: List of values at each node.
        :return: A list [length of longest special path, minimum number of nodes in all longest special paths].
        """
        # Build the adjacency list
        adj = [[] for _ in range(len(nums))]
        for u, v, length in edges:
            adj[u].append((v, length))
            adj[v].append((u, length))

        # DFS to find the longest special path and the minimum number of nodes in all longest special paths
        def dfs(node, parent):
            max1, max2 = 0, 0
            for neighbor, length in adj[node]:
                if neighbor == parent:
                    continue
                length1, length2 = dfs(neighbor, node)
                if nums[node] != nums[neighbor]:
                    if length1 > length2:
                        length1, length2 = length2, length1
                    if length1 > max1:
                        max2 = max1
                        max1 = length1
                    elif length1 > max2:
                        max2 = length1
            self.longest = max(self.longest, max1 + max2 + 1)
            return max1 + 1, max2 + 1

        # Start DFS from node 0
        dfs(0, -1)
        return [self.longest, 1]

class Solution:
    def longestSpecialPath(self, edges: List[List[int]], nums: List[int]) -> List[int]:
        """
        Finds the length of the longest special path in a tree and the minimum number of nodes in all possible longest special paths.

        :param edges: List of edges representing the tree.
        :param nums: List of values at each node.
        :return: List containing the length of the longest special path and the minimum number of nodes in all possible longest special paths.
        """
        from collections import defaultdict, deque

        # Build the graph
        graph = defaultdict(list)
        for u, v, length in edges:
            graph[u].append((v, length))
            graph[v].append((u, length))

        # Initialize the result array
        result = [0, float('inf')]

        # BFS to find the longest special path
        queue = deque()
        for node in range(len(nums)):
            if len(graph[node]) == 1:
                queue.append((node, nums[node]))

        while queue:
            node, length = queue.popleft()
            children = graph[node]
            if len(children) == 1:
                child, child_length = children[0]
                if nums[child] != nums[node]:
                    result[0] = max(result[0], length + child_length)
                    result[1] = min(result[1], 1)
                else:
                    queue.append((child, length + child_length))
            else:
                children.sort(key=lambda x: x[1], reverse=True)
                if nums[children[0][0]] != nums[node]:
                    result[0] = max(result[0], length + children[0][1])
                    result[1] = min(result[1], 2)
                else:
                    queue.append((children[0][0], length + children[0][1]))
                if len(children) > 1:
                    if nums[children[1][0]] != nums[node]:
                        result[0] = max(result[0], length + children[1][1])
                        result[1] = min(result[1], 2)
                    else:
                        queue.append((children[1][0], length + children[1][1]))

        return result

