class Solution:
    def minimumIncrements(self, nums: List[int], target: List[int]) -> int:
        """
        Returns the minimum number of operations required so that each element in target has at least one multiple in nums.

        :param nums: List of integers representing the array to be incremented.
        :param target: List of integers representing the target array.
        :return: Integer representing the minimum number of operations.
        """
        target_counts = {}
        for num in target:
            target_counts[num] = target_counts.get(num, 0) + 1

        nums.sort()
        operations = 0
        i = 0
        for num in target:
            while i < len(nums) and nums[i] < num:
                i += 1
            if i == len(nums) or nums[i] > num:
                operations += num - nums[i - 1] + 1
                nums[i - 1] += num - nums[i - 1] + 1
            else:
                operations += num - nums[i]
                nums[i] = num
        return operations
