    def minCostGoodCaption(self, caption: str) -> str:
        """
        Converts the given caption into a good caption using the minimum number of operations.
        Returns the lexicographically smallest good caption if possible, otherwise returns an empty string.
        """
        n = len(caption)
        if n < 3:
            return caption

        def next_char(c):
            if c == 'z':
                return 'a'
            else:
                return chr(ord(c) + 1)

        def prev_char(c):
            if c == 'a':
                return 'z'
            else:
                return chr(ord(c) - 1)

        def is_good(caption):
            for i in range(1, n):
                if caption[i] != caption[i-1]:
                    return False
            return True

        def get_min_operations(caption):
            operations = 0
            for i in range(1, n):
                if caption[i] != caption[i-1]:
                    operations += 1
            return operations

        if is_good(caption):
            return caption

        operations = get_min_operations(caption)
        if operations > 2:
            return ""

        for i in range(1, n):
            if caption[i] != caption[i-1]:
                if caption[i] == 'a':
                    caption = caption[:i] + next_char(caption[i]) + caption[i+1:]
                elif caption[i] == 'z':
                    caption = caption[:i] + prev_char(caption[i]) + caption[i+1:]

