class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        """
        Converts the given caption into a good caption using the minimum number of operations.
        Returns the lexicographically smallest good caption if possible, otherwise returns an empty string.
        """
        n = len(caption)
        if n < 3:
            return caption

        def next_char(c):
            if c == 'a':
                return 'b'
            elif c == 'z':
                return 'y'
            else:
                return chr(ord(c) + 1)

        def prev_char(c):
            if c == 'a':
                return 'z'
            elif c == 'z':
                return 'y'
            else:
                return chr(ord(c) - 1)

        def is_good(caption):
            for i in range(1, n):
                if caption[i] != caption[i - 1]:
                    return False
            return True

        def get_min_operations(caption):
            operations = 0
            for i in range(1, n):
                if caption[i] != caption[i - 1]:
                    operations += 1
            return operations

        if is_good(caption):
            return caption

        min_operations = float('inf')
        min_caption = ''

        for i in range(n):
            for j in range(i + 2, n + 1):
                new_caption = caption[:i] + next_char(caption[i]) + caption[i + 1:j] + prev_char(caption[j - 1]) + caption[j:]
                if is_good(new_caption):
                    operations = get_min_operations(new_caption)
                    if operations < min_operations:
                        min_operations = operations
                        min_caption = new_caption
                    elif operations == min_operations:
                        if new_caption < min_caption:
                            min_caption = new_caption

        return min_caption if min_operations != float('inf') else ''
