class Solution:
    def minCostGoodCaption(self, caption: str) -> str:
        """
        Converts the given caption into a good caption using the minimum number of operations.
        Returns the lexicographically smallest good caption if possible, otherwise returns an empty string.
        """
        n = len(caption)
        if n < 3:
            return caption

        def is_good(s):
            """
            Checks if the given string s is a good caption.
            """
            for i in range(1, len(s)):
                if s[i] != s[i-1]:
                    return False
            return True

        def next_char(c):
            """
            Returns the next character in the alphabet.
            """
            return chr(ord(c) + 1) if c != 'z' else 'a'

        def prev_char(c):
            """
            Returns the previous character in the alphabet.
            """
            return chr(ord(c) - 1) if c != 'a' else 'z'

        def can_be_good(caption):
            """
            Checks if the given caption can be transformed into a good caption using the minimum number of operations.
            Returns the lexicographically smallest good caption if possible, otherwise returns an empty string.
            """
            n = len(caption)
            if n < 3:
                return caption

            dp = [[float('inf')] * 26 for _ in range(n)]
            for i in range(n):
                dp[i][ord(caption[i]) - ord('a')] = 0

            for i in range(n-2, -1, -1):
                for j in range(26):
                    if dp[i][j] == float('inf'):
                        continue
                    if j == ord(caption[i]) - ord('a'):
                        dp[i][j] = min(dp[i][j], dp[i+1][j] + 1)
                    else:
                        dp[i][j] = min(dp[i][j], dp[i+1][ord(caption[i]) - ord('a')] + 1)

            min_cost = float('inf')
            best_caption = ""
            for j in range(26):
                if dp[0][j] < min_cost:
                    min_cost = dp[0][j]
                    best_caption = chr(ord('a') + j) * n

            for i in range(1, n-1):
                for j in range(26):
                    if dp[i][j] == float('inf'):
                        continue
                    if j == ord(caption[i]) - ord('a'):
                        dp[i][j] = min(dp[i][j], dp[i+1][j] + 1)
                    else:
                        dp[i][j] = min(dp[i][j], dp[i+1][ord(caption[i]) - ord('a')] + 1)

            for j in range(26):
                if dp[n-1][j] < min_cost:
                    min_cost = dp[n-1][j]
                    best_caption = best_caption[:i] + chr(ord('a') + j) + best_caption[i+1:]

            return best_caption if is_good(best_caption) else ""

        return can_be_good(caption)
