    def minCostGoodCaption(self, caption: str) -> str:
        """
        Converts the given caption into a good caption using the minimum number of operations.
        If it is impossible to create a good caption, returns an empty string.

        :param caption: A string representing the caption.
        :return: A good caption or an empty string if it is impossible.
        """
        if not caption:
            return ""

        n = len(caption)
        dp = [[float('inf')] * 26 for _ in range(n)]
        dp[0][ord(caption[0]) - ord('a')] = 0

        for i in range(1, n):
            for j in range(26):
                if dp[i - 1][j] != float('inf'):
                    if j == ord(caption[i]) - ord('a'):
                        dp[i][j] = dp[i - 1][j]
                    else:
                        dp[i][j] = dp[i - 1][j] + 1
            if caption[i] != 'a':
                dp[i][ord(caption[i]) - ord('a')] = min(dp[i][ord(caption[i]) - ord('a')], dp[i - 1][ord(caption[i]) - ord('a') - 1] + 1)
            if caption[i] != 'z':
                dp[i][ord(caption[i]) - ord('a')] = min(dp[i][ord(caption[i]) - ord('a')], dp[i - 1][ord(caption[i]) - ord('a') + 1] + 1)

        min_operations = float('inf')
        best_caption = ""
        for j in range(26):
            if dp[n - 1][j] != float('inf'):
                operations = dp[n - 1][j]
                if operations < min_operations:
                    min_operations = operations
                    best_caption = chr(j + ord('a'))
                elif operations == min_operations:
                    best_caption = min(best_caption, chr(j + ord('a')))

        return best_caption

