class Solution:
    def minMaxSums(self, nums: List[int], k: int) -> int:
        """
        Computes the sum of the maximum and minimum elements of all subsequences of nums with at most k elements.

        :param nums: List of integers representing the array.
        :param k: Positive integer representing the maximum length of subsequences.
        :return: Sum of the maximum and minimum elements of all subsequences with at most k elements, modulo 10^9 + 7.
        """
        nums.sort()
        total = 0
        for i in range(k):
            total += nums[i] + nums[-(i + 1)]
        return total % (10**9 + 7)

