class Solution:
    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        """
        Finds the maximum sum of k non-overlapping subarrays of nums, where each subarray has a length of at least m.

        :param nums: List[int] - The input array of integers.
        :param k: int - The number of subarrays to choose.
        :param m: int - The minimum length of each subarray.
        :return: int - The maximum sum of the chosen subarrays.
        """
        n = len(nums)
        if n < k * m:
            return float('-inf')

        dp = [[0] * (n + 1) for _ in range(k + 1)]
        prefix_sum = [0] * (n + 1)

        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]

        for i in range(1, k + 1):
            for j in range(i * m, n + 1):
                dp[i][j] = max(dp[i][j - 1], prefix_sum[j] - prefix_sum[j - i * m] + dp[i - 1][j - i * m])

        return dp[k][n]
