    def sumOfGoodNumbers(self, nums: List[int], k: int) -> int:
        """
        Returns the sum of all the good elements in the array.
        An element is considered good if it is strictly greater than the elements at indices i - k and i + k.
        If neither of these indices exists, the element is still considered good.
        """
        n = len(nums)
        total_sum = 0

        for i in range(n):
            left = max(0, i - k)
            right = min(n - 1, i + k)

            if left == i - k and right == i + k:
                total_sum += nums[i]
            elif left == i - k:
                total_sum += nums[i] if nums[i] > nums[right] else 0
            elif right == i + k:
                total_sum += nums[i] if nums[i] > nums[left] else 0
            else:
                total_sum += nums[i] if nums[i] > nums[left] and nums[i] > nums[right] else 0

        return total_sum
