class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        """
        Finds the length of the longest V-shaped diagonal segment in a 2D grid.

        :param grid: List[List[int]] - A 2D grid of integers where each element is either 0, 1, or 2.
        :return: int - The length of the longest V-shaped diagonal segment.
        """
        def is_valid_segment(x, y, direction):
            """
            Checks if a segment starting at (x, y) in the given direction is valid.

            :param x: int - The starting x-coordinate.
            :param y: int - The starting y-coordinate.
            :param direction: str - The direction of the segment ('top-left to bottom-right', 'bottom-right to top-left', 'top-right to bottom-left', 'bottom-left to top-right').
            :return: bool - True if the segment is valid, False otherwise.
            """
            if direction == 'top-left to bottom-right':
                if grid[x][y] != 1:
                    return False
                for i in range(1, 1000):
                    if grid[x + i][y + i] != 2:
                        return False
                    if grid[x + i][y + i] == 0:
                        return True
            elif direction == 'bottom-right to top-left':
                if grid[x][y] != 1:
                    return False
                for i in range(1, 1000):
                    if grid[x - i][y + i] != 2:
                        return False
                    if grid[x - i][y + i] == 0:
                        return True
            elif direction == 'top-right to bottom-left':
                if grid[x][y] != 1:
                    return False
                for i in range(1, 1000):
                    if grid[x + i][y - i] != 2:
                        return False
                    if grid[x + i][y - i] == 0:
                        return True
            elif direction == 'bottom-left to top-right':
                if grid[x][y] != 1:
                    return False
                for i in range(1, 1000):
                    if grid[x - i][y - i] != 2:
                        return False
                    if grid[x - i][y - i] == 0:
                        return True
            return False

        def find_longest_segment(x, y, direction):
            """
            Finds the length of the longest V-shaped diagonal segment starting at (x, y) in the given direction.

            :param x: int - The starting x-coordinate.
            :param y: int - The starting y-coordinate.
            :param direction: str - The direction of the segment ('top-left to bottom-right', 'bottom-right to top-left', 'top-right to bottom-left', 'bottom-left to top-right').
            :return: int - The length of the longest V-shaped diagonal segment.
            """
            max_length = 0
            while True:
                if not is_valid_segment(x, y, direction):
                    break
                max_length += 1
                if direction == 'top-left to bottom-right':
                    x += 1
                    y += 1
                elif direction == 'bottom-right to top-left':
                    x -= 1
                    y += 1
                elif direction == 'top-right to bottom-left':
                    x += 1
                    y -= 1
                elif direction == 'bottom-left to top-right':
                    x -= 1
                    y -= 1
            return max_length

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, find_longest_segment(i, j, 'top-left to bottom-right'))
                    max_length = max(max_length, find_longest_segment(i, j, 'bottom-right to top-left'))
                    max_length = max(max_length, find_longest_segment(i, j, 'top-right to bottom-left'))
                    max_length = max(max_length, find_longest_segment(i, j, 'bottom-left to top-right'))
        return max_length
