                for i in range(1, len(grid) - x - 1):
                    if grid[x + i][y + i] != 2 or grid[x + i][y + i + 1] != 0:
                        return False
                return True
            elif direction == 1:
                if x + 1 >= len(grid) or y - 1 < 0 or grid[x][y] != 1 or grid[x + 1][y - 1] != 2:
                    return False
                for i in range(1, len(grid) - x - 1):
                    if grid[x + i][y - i] != 2 or grid[x + i][y - i - 1] != 0:
                        return False
                return True
            elif direction == 2:
                if x - 1 < 0 or y + 1 >= len(grid[0]) or grid[x][y] != 1 or grid[x - 1][y + 1] != 2:
                    return False
                for i in range(1, len(grid) - x - 1):
                    if grid[x - i][y + i] != 2 or grid[x - i][y + i + 1] != 0:
                        return False
                return True
            elif direction == 3:
                if x - 1 < 0 or y - 1 < 0 or grid[x][y] != 1 or grid[x - 1][y - 1] != 2:
                    return False
                for i in range(1, len(grid) - x - 1):
                    if grid[x - i][y - i] != 2 or grid[x - i][y - i - 1] != 0:
                        return False
                return True
            return False

        def find_longest_segment():
            """
            Finds the length of the longest V-shaped diagonal segment in the grid.

            :return: Length of the longest V-shaped diagonal segment.
            """
            max_length = 0
            for i in range(len(grid)):
                for j in range(len(grid[0])):
                    if grid[i][j] == 1:
                        for direction in range(4):
                            if is_valid_segment(i, j, direction):
                                length = 1
                                x, y = i, j
                                while True:
                                    x += 1
                                    y += 1
                                    if x >= len(grid) or y >= len(grid[0]) or grid[x][y] != 2:
                                        break
                                    length += 1
                                max_length = max(max_length, length)
            return max_length

        return find_longest_segment()

class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        """
        Returns the length of the longest V-shaped diagonal segment in the given 2D grid.

        :param grid: List[List[int]] - A 2D grid of integers where each element is either 0, 1, or 2.
        :return: int - The length of the longest V-shaped diagonal segment.
        """
        def is_valid_segment(x, y, direction, sequence):
            """
            Checks if the segment starting at (x, y) in the given direction follows the specified sequence.

            :param x: int - The starting x-coordinate of the segment.
            :param y: int - The starting y-coordinate of the segment.
            :param direction: str - The direction of the segment ('top-left to bottom-right', 'bottom-right to top-left', 'top-right to bottom-left', 'bottom-left to top-right').
            :param sequence: List[int] - The sequence of elements that the segment should follow.
            :return: bool - True if the segment is valid, False otherwise.
            """
            if direction == 'top-left to bottom-right':
                dx, dy = 1, 1
            elif direction == 'bottom-right to top-left':
                dx, dy = -1, -1
            elif direction == 'top-right to bottom-left':
                dx, dy = -1, 1
            elif direction == 'bottom-left to top-right':
                dx, dy = 1, -1
            else:
                return False

            for i in range(len(sequence)):
                nx, ny = x + i * dx, y + i * dy
                if nx < 0 or nx >= len(grid) or ny < 0 or ny >= len(grid[0]) or grid[nx][ny] != sequence[i]:
                    return False
            return True

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    for direction in ['top-left to bottom-right', 'bottom-right to top-left', 'top-right to bottom-left', 'bottom-left to top-right']:
                        for sequence in [[2], [2, 0], [2, 0, 2, 0]]:
                            if is_valid_segment(i, j, direction, sequence):
                                max_length = max(max_length, len(sequence))
        return max_length

class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        """
        Finds the length of the longest V-shaped diagonal segment in a 2D grid.

        :param grid: List of lists of integers representing the grid.
        :return: Length of the longest V-shaped diagonal segment.
        """
        def countVDiagonal(start_row, start_col, direction):
            """
            Counts the length of a V-shaped diagonal segment starting from a given position and direction.

            :param start_row: Row index of the starting position.
            :param start_col: Column index of the starting position.
            :param direction: Direction of the diagonal (0 for top-left to bottom-right, 1 for bottom-right to top-left, 2 for top-right to bottom-left, 3 for bottom-left to top-right).
            :return: Length of the V-shaped diagonal segment.
            """
            if grid[start_row][start_col] != 1:
                return 0

            length = 1
            while True:
                next_row = start_row + directions[direction][0]
                next_col = start_col + directions[direction][1]

                if not (0 <= next_row < len(grid) and 0 <= next_col < len(grid[0])):
                    return length

                if grid[next_row][next_col] == 2:
                    length += 1
                elif grid[next_row][next_col] == 0:
                    return length
                else:
                    break

            return length

        directions = [
            (1, 0),  # top-left to bottom-right
            (0, 1),  # bottom-right to top-left
            (-1, 0), # top-right to bottom-left
            (0, -1)  # bottom-left to top-right
        ]

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                for direction in range(4):
                    length = countVDiagonal(i, j, direction)
                    max_length = max(max_length, length)

        return max_length

