class Solution:
    def sortMatrix(self, grid: List[List[int]]) -> List[List[int]]:
        """
        Sorts the diagonals of a given n x n matrix in a specific order.

        :param grid: List of lists of integers representing the matrix.
        :return: Sorted matrix with specified diagonal order.
        """
        n = len(grid)
        diagonals = []

        for i in range(n):
            diagonals.append([])
            for j in range(i, n):
                diagonals[i].append(grid[j][i - j])

        for i in range(1, n):
            for j in range(i):
                diagonals[i].insert(j, grid[i - 1][j - i + 1])

        for i in range(n):
            diagonals[i].sort(reverse=True)

        for i in range(n):
            for j in range(i):
                diagonals[i].insert(j, grid[i - 1][j - i + 1])

        result = [[0] * n for _ in range(n)]

        for i in range(n):
            for j in range(i, n):
                result[i][j - i] = diagonals[i][j - i]

        for i in range(1, n):
            for j in range(i):
                result[i][j - i] = diagonals[i][j - i]

        return result
