            diagonals.append([])
            for j in range(n):
                if i == j:
                    diagonals[i].append(grid[i][j])

        for i in range(n):
            diagonals[i].sort(reverse=True)

        for i in range(n):
            for j in range(n):
                if i == j:
                    grid[i][j] = diagonals[i][0]
                    diagonals[i].pop(0)

        for i in range(n):
            for j in range(n):
                if i + j == n - 1:
                    grid[i][j] = diagonals[i][0]
                    diagonals[i].pop(0)

        return grid

