            indices = index_map[nums[q]]
            min_distance = float('inf')
            for i in indices:
                if i == q:
                    continue
                distance = abs(i - q)
                distance = min(distance, n - distance)
                min_distance = min(min_distance, distance)

            result.append(min_distance if min_distance != float('inf') else -1)

        return result

class Solution:
    def solveQueries(self, nums: List[int], queries: List[int]) -> List[int]:
        """
        Given a circular array nums and an array of queries, returns an array of minimum distances
        between the element at each query index and any other index with the same value in the circular array.
        """
        n = len(nums)
        result = [-1] * len(queries)
        index_map = {}

        for i in range(n):
            if nums[i] not in index_map:
                index_map[nums[i]] = []
            index_map[nums[i]].append(i)

        for i, q in enumerate(queries):
            if nums[q] in index_map:
                for j in index_map[nums[q]]:
                    if j != q:
                        result[i] = abs(q - j)
                        break
            else:
                result[i] = -1

        return result

