class Solution:
    def maxDistance(self, s: str, k: int) -> int:
        """
        Finds the maximum Manhattan distance from the origin that can be achieved
        by changing at most k characters in the string s.

        :param s: A string consisting of 'N', 'S', 'E', and 'W'.
        :param k: An integer representing the maximum number of character changes.
        :return: The maximum Manhattan distance from the origin.
        """
        n = len(s)
        max_dist = 0
        left = 0
        right = 0
        count = 0
        max_count = 0

        while right < n:
            if s[right] == 'N':
                count += 1
            elif s[right] == 'S':
                count -= 1
            elif s[right] == 'E':
                count += 1
            elif s[right] == 'W':
                count -= 1

            while count > k:
                if s[left] == 'N':
                    count -= 1
                elif s[left] == 'S':
                    count += 1
                elif s[left] == 'E':
                    count -= 1
                elif s[left] == 'W':
                    count += 1
                left += 1

            max_count = max(max_count, count)
            max_dist = max(max_dist, right - left + 1)
            right += 1

        return max_dist
