class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        """
        Finds the minimum y-coordinate value of a horizontal line such that the total area of the squares above the line
        equals the total area of the squares below the line. Answers within 10^-5 of the actual answer will be accepted.

        :param squares: List of squares, where each square is represented as [x_i, y_i, l_i] with coordinates of the bottom-left point and side length.
        :return: Minimum y-coordinate value of the horizontal line.
        """
        squares.sort(key=lambda x: x[1])  # Sort by y-coordinate
        total_area = sum(l * l for x, y, l in squares)  # Calculate total area of all squares
        left, right = 0, 10**10  # Initialize search range

        while right - left > 1e-5:
            mid = (left + right) / 2
            above_area = 0
            below_area = 0
            for x, y, l in squares:
                if y + l > mid:
                    above_area += l * l
                else:
                    below_area += l * l
            if above_area == below_area:
                return mid
            elif above_area < below_area:
                left = mid
            else:
                right = mid

        return (left + right) / 2
