class Solution:
    def maxSum(self, grid: List[List[int]], limits: List[int], k: int) -> int:
        """
        Finds the maximum sum of at most k elements from the matrix grid
        such that the number of elements taken from the i-th row does not exceed limits[i].

        :param grid: List of lists of integers representing the matrix.
        :param limits: List of integers representing the maximum number of elements that can be taken from each row.
        :param k: Integer representing the maximum number of elements to take.
        :return: Integer representing the maximum sum of at most k elements.
        """
        n = len(grid)
        m = len(grid[0])
        dp = [[0] * (k + 1) for _ in range(n)]

        for i in range(n):
            for j in range(m):
                if i == 0:
