class Solution:
    def minimumPairRemoval(self, nums: List[int]) -> int:
        """
        Returns the minimum number of operations needed to make the array non-decreasing.
        An array is non-decreasing if each element is greater than or equal to its previous element (if it exists).
        """
        def is_non_decreasing(arr):
            """
            Checks if the array is non-decreasing.
            """
            for i in range(1, len(arr)):
                if arr[i] < arr[i - 1]:
                    return False
            return True

        def min_operations_to_non_decreasing(arr):
            """
            Returns the minimum number of operations needed to make the array non-decreasing.
            """
            n = len(arr)
            dp = [[float('inf')] * n for _ in range(n)]
            for i in range(n):
                dp[i][i] = 0

            for length in range(2, n + 1):
                for i in range(n - length + 1):
                    j = i + length - 1
                    for k in range(i, j):
                        left_sum = dp[i][k]
                        right_sum = dp[k + 1][j]
                        dp[i][j] = min(dp[i][j], left_sum + right_sum + 1)

            return dp[0][n - 1]

        return min_operations_to_non_decreasing(nums)
