class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers.
        If there are fewer than k valid alternating permutations, returns an empty list.
        """
        def generate_alternating_permutations(n):
            """
            Generates all alternating permutations of the first n positive integers.
            """
            if n == 1:
                return [[1]]
            prev_permutations = generate_alternating_permutations(n - 1)
            result = []
            for perm in prev_permutations:
                for i in range(len(perm) + 1):
                    result.append(perm[:i] + [n] + perm[i:])
            return result

        def count_alternating_permutations(n):
            """
            Counts the number of alternating permutations of the first n positive integers.
            """
            if n == 1:
                return 1
            return 2 * count_alternating_permutations(n - 1)

        if k > count_alternating_permutations(n):
            return []

        def find_kth_alternating_permutation(n, k):
            """
            Finds the k-th alternating permutation of the first n positive integers.
            """
            if n == 1:
                return [1]
            if k % 2 == 0:
                return [n] + find_kth_alternating_permutation(n - 1, k // 2)
            else:
                return [n] + find_kth_alternating_permutation(n - 1, (k - 1) // 2)

        return find_kth_alternating_permutation(n, k)
