class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers.
        If there are fewer than k valid alternating permutations, returns an empty list.
        """
        def generate_permutations(n):
            if n == 1:
                return [1]
            prev_permutations = generate_permutations(n - 1)
            result = []
            for perm in prev_permutations:
                for i in range(len(perm) + 1):
                    result.append(perm[:i] + [n] + perm[i:])
            return result

        def is_alternating(perm):
            """
            Checks if a permutation is alternating.
            """
            for i in range(len(perm) - 1):
                if (perm[i] % 2 == 0 and perm[i + 1] % 2 == 0) or (perm[i] % 2 != 0 and perm[i + 1] % 2 != 0):
                    return False
            return True

        permutations = generate_permutations(n)
        permutations.sort()
        count = 0
        for perm in permutations:
            if is_alternating(perm):
                count += 1
                if count == k:
                    return perm
        return []
