class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers.
        If there are fewer than k valid alternating permutations, returns an empty list.
        """
        def is_valid_permutation(arr):
            """
            Checks if the given array is a valid alternating permutation.
            """
            for i in range(1, len(arr)):
                if (arr[i] % 2 == arr[i - 1] % 2):
                    return False
            return True

        def generate_alternating_permutations(n):
            """
            Generates all valid alternating permutations of the first n positive integers.
            """
            def backtrack(start, path):
                if len(path) == n:
                    if is_valid_permutation(path):
                        result.append(path[:])
                    return
                for i in range(start, n + 1):
                    path.append(i)
                    backtrack(i + 1, path)
                    path.pop()

