class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers
        sorted in lexicographical order. If there are fewer than k valid
        alternating permutations, returns an empty list.
        """
        if k < 1 or k > self.count_alternating_permutations(n):
            return []

        return self.get_kth_alternating_permutation(n, k)

    def count_alternating_permutations(self, n: int) -> int:
        """
        Counts the number of valid alternating permutations of the first n positive integers.
        """
        count = 0
        for i in range(1, n + 1):
            if i % 2 == 1:
                count += self.count_alternating_permutations(n - 1)
            else:
                count += self.count_alternating_permutations(n - 1) * 2
        return count

    def get_kth_alternating_permutation(self, n: int, k: int) -> List[int]:
        """
        Returns the k-th alternating permutation of the first n positive integers
        sorted in lexicographical order.
        """
        if n == 1:
            return [k]

        if k <= self.count_alternating_permutations(n - 1):
            return [n] + self.get_kth_alternating_permutation(n - 1, k)

        k -= self.count_alternating_permutations(n - 1)
        if k % 2 == 0:
            return [n] + self.get_kth_alternating_permutation(n - 1, k // 2)
        else:
            return [n - 1] + self.get_kth_alternating_permutation(n - 1, (k + 1) // 2)
