class Solution:
    def longestCommonPrefix(self, words: List[str], k: int) -> List[int]:
        """
        Finds the longest common prefix for each index i in the range [0, words.length - 1],
        where the prefix is among any k strings (selected at distinct indices) from the remaining array
        after removing the i^th element. Returns an array answer, where answer[i] is the answer for i^th element.
        If removing the i^th element leaves the array with fewer than k strings, answer[i] is 0.
        """
        n = len(words)
        answer = [0] * n

        for i in range(n):
            if k > 1:
                words = [word for j, word in enumerate(words) if j != i]
                k -= 1
            else:
                break

            if not words:
                break

            prefix = words[0]
            for word in words[1:]:
                while word[:len(prefix)] != prefix:
                    prefix = prefix[:-1]
                    if not prefix:
                        break

            answer[i] = len(prefix)

        return answer
