class Solution:
    def countArrays(self, original: List[int], bounds: List[List[int]]) -> int:
        """
        Counts the number of arrays copy of length n such that:
        (copy[i] - copy[i - 1]) == (original[i] - original[i - 1]) for 1 <= i <= n - 1.
        u_i <= copy[i] <= v_i for 0 <= i <= n - 1.
        """
        n = len(original)
        if n == 0:
            return 0

        min_diff = float('inf')
        max_diff = float('-inf')

        for i in range(1, n):
            diff = original[i] - original[i - 1]
            min_diff = min(min_diff, diff)
            max_diff = max(max_diff, diff)

        if min_diff == float('inf'):
            return 0

        dp = [0] * (max_diff - min_diff + 1)
        dp[0] = 1

        for i in range(1, n):
            diff = original[i] - original[i - 1]
            for j in range(min_diff, max_diff + 1):
                if j - diff >= 0:
                    dp[j - min_diff] += dp[j - min_diff - diff]

        return sum(dp)
