class Solution:
    def countArrays(self, original: List[int], bounds: List[List[int]]) -> int:
        """
        Counts the number of arrays copy of length n such that:
        (copy[i] - copy[i - 1]) == (original[i] - original[i - 1]) for 1 <= i <= n - 1.
        u_i <= copy[i] <= v_i for 0 <= i <= n - 1.
        """
        n = len(original)
        if n == 1:
            return 1 if bounds[0][0] <= original[0] <= bounds[0][1] else 0

        dp = [[0] * (bounds[-1][1] + 1) for _ in range(n)]
        dp[0][original[0]] = 1

        for i in range(1, n):
            for j in range(bounds[i][0], bounds[i][1] + 1):
                if j == original[i]:
                    dp[i][j] = dp[i - 1][j]
                elif j > original[i]:
                    dp[i][j] = dp[i - 1][j]
                else:
                    dp[i][j] = dp[i - 1][j] + dp[i - 1][j + 1]

        return sum(dp[-1])
