class Solution:
    def numberOfComponents(self, properties: List[List[int]], k: int) -> int:
        """
        Returns the number of connected components in the graph formed by the properties array.
        """
        from collections import defaultdict

        # Create a dictionary to store the properties for each index
        properties_dict = defaultdict(list)
        for i, prop in enumerate(properties):
            properties_dict[i] = prop

        # Create a set to store the visited nodes
        visited = set()

        # Function to perform DFS
        def dfs(node):
            if node in visited:
                return
            visited.add(node)
            for neighbor in properties_dict[node]:
                if neighbor not in visited:
                    dfs(neighbor)

        # Count the number of connected components
        count = 0
        for node in properties_dict:
            if node not in visited:
                dfs(node)
                count += 1

        return count
