class Solution:
    def numberOfComponents(self, properties: List[List[int]], k: int) -> int:
        """
        Returns the number of connected components in the graph formed by the properties array.
        Each node in the graph corresponds to an index in the properties array.
        An edge exists between two nodes if and only if the intersection of their properties is at least k.
        """
        from collections import defaultdict

        graph = defaultdict(set)
        properties_set = set(tuple(p) for p in properties)

        for i in range(len(properties)):
