class Solution:
    def minCosts(self, cost: List[int]) -> List[int]:
        """
        Returns an array answer of size n, where answer[i] is the minimum total cost to reach each position i in the line.
        """
        n = len(cost)
        if n == 0:
            return []

        dp = [0] * (n + 1)

        for i in range(n - 1, -1, -1):
            if i == n - 1:
                dp[i] = cost[i]
            else:
                dp[i] = cost[i] + min(dp[i + 1], dp[i + 2])

        return dp
