def main():
    """
    Reads input from standard input, processes the data to determine if it is possible to move from square 1 to square N,
    and prints "Yes" if it is possible, otherwise prints "No".
    """
    import sys
    input = sys.stdin.read().split()
    N = int(input[0])
    M = int(input[1])
    A = int(input[2])
    B = int(input[3])
    index = 4
    pairs = []
    for _ in range(M):
        L = int(input[index])
        R = int(input[index + 1])
        pairs.append((L, R))
        index += 2
    current = 1
    while current <= N:
        for L, R in pairs:
            if L <= current <= R:
                if current + B >= N:
                    break
                if current + A <= current + B:
                    current += B
                    break
        else:
            break
    if current == N:
        print("Yes")
    else:
        print("No")

