def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def count_connected_graphs(N, P):
    """Count the number of connected graphs with N vertices and M edges
    where the number of vertices at even distance from vertex 1 is equal
    to the number of vertices at odd distance from vertex 1, modulo P."""
    if not is_prime(P):
        raise ValueError("P must be a prime number.")
    if N % 2 != 0:
        raise ValueError("N must be an even number.")

    MOD = P
    count = 0
    for M in range((N * (N - 1)) // 2, N - 1, -1):
        if M % 2 == 0:
            continue
        count += 1
        if count % MOD == 0:
            break
    return count % MOD

