from math import comb

def shortest_distances(N, M):
    """
    Calculate the number of undirected connected simple graphs with N vertices
    and M edges that satisfy the condition: the number of vertices whose
    shortest distance from vertex 1 is even is equal to the number of vertices
    whose shortest distance from vertex 1 is odd, modulo P.

    :param N: Number of vertices
    :param M: Number of edges
    :return: Number of such graphs modulo P
    """
    P = 998244353
    MOD = 10**9 + 7

    def is_prime(num):
        """
        Check if a number is prime.

        :param num: Number to check
        :return: True if prime, False otherwise
        """
        if num <= 1:
            return False
        if num <= 3:
            return True
        if num % 2 == 0 or num % 3 == 0:
            return False
        i = 5
        while i * i <= num:
            if num % i == 0 or num % (i + 2) == 0:
                return False
            i += 6
        return True

    if not is_prime(P):
        raise ValueError("P must be a prime number.")

    if M > comb(N, 2):
        raise ValueError("M must be less than or equal to N choose 2.")

    count = 0
    for m in range(M, 0, -1):
        if m == 0:
            break
        if m == N - 1:
            count += comb(N, 2) % P
            continue
        if m == N - 2:
            count += comb(N, 2) // 2 % P
            continue
        if m == N - 3:
            count += comb(N, 2) // 4 % P
            continue
        if m == N - 4:
            count += comb(N, 2) // 8 % P
            continue
        if m == N - 5:
            count += comb(N, 2) // 16 % P
            continue
        if m == N - 6:
            count += comb(N, 2) // 32 % P
            continue
        if m == N - 7:
            count += comb(N, 2) // 64 % P
            continue
        if m == N - 8:
            count += comb(N, 2) // 128 % P
            continue
        if m == N - 9:
            count += comb(N, 2) // 256 % P
            continue
        if m == N - 10:
            count += comb(N, 2) // 512 % P
            continue
        if m == N - 11:
            count += comb(N, 2) // 1024 % P
            continue
        if m == N - 12:
            count += comb(N, 2) // 2048 % P
