def is_possible_to_paint_rectangle(grid):
    """
    Determines if it is possible to paint all not-yet-painted cells in the grid
    so that the black cells form a rectangle.

    :param grid: List of strings representing the grid.
    :return: True if it is possible to form a rectangle, False otherwise.
    """
    H = len(grid)
    W = len(grid[0]) if H > 0 else 0

    if H == 0 or W == 0:
        return False

    black_cells = []
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                black_cells.append((i, j))

    if len(black_cells) == 0:
        return False

    black_cells.sort()

    for i in range(1, len(black_cells)):
        if black_cells[i][0] - black_cells[i-1][0] > 1:
            return False

    for j in range(1, len(black_cells[0])):
        if black_cells[0][1] + j - 1 >= W or black_cells[-1][1] + j - 1 >= W:
            return False
        for k in range(1, len(black_cells)):
            if black_cells[k][1] - black_cells[k-1][1] > j:
                return False

    return True

