def main():
    import sys
    input = sys.stdin.read().split()
    N, W = map(int, input[:2])
    blocks = [(int(input[2*i+2]), int(input[2*i+3])) for i in range(N)]
    Q = int(input[2*N+2])
    times = [int(input[2*N+3+2*i]) for i in range(Q)]
    queries = [int(input[2*N+3+2*i+1]) for i in range(Q)]

    def simulate_blocks(blocks, times, queries):
        """
        Simulates the movement of blocks on the grid and determines if a specific block exists at a given time.

        :param blocks: List of tuples representing the positions of the blocks.
        :param times: List of times at which to check the existence of blocks.
        :param queries: List of block indices to check.
        :return: List of strings "Yes" or "No" indicating the existence of blocks at the specified times.
        """
        grid = [[0] * W for _ in range(10**9)]
        for x, y in blocks:
            grid[y-1][x-1] = 1

        for t in times:
            if t == 1:
                for y in range(10**9):
                    if all(grid[y][x] == 1 for x in range(W)):
                        for x in range(W):
                            grid[y][x] = 0
                        break
            else:
                for y in range(10**9-1, 0, -1):
                    if grid[y][0] == 1 and (y == 10**9 or grid[y-1][0] == 1):
                        for x in range(W):
                            grid[y][x] = 0
                        break
                    elif grid[y][0] == 1:
                        for x in range(1, W):
                            if grid[y][x] == 1:
                                grid[y][x-1] = 1
                                grid[y][x] = 0
                                break
                        else:
                            grid[y][0] = 0
                            break

        results = []
        for q in queries:
            x, y = blocks[q-1]
            if grid[y-1][x-1] == 1:
                results.append("Yes")
            else:
                results.append("No")
        return results

    results = simulate_blocks(blocks, times, queries)
    for result in results:
        print(result)

