def generate_pattern(N):
    """
    Generates the N x N pattern as specified in the problem description.

    Parameters:
    N (int): The size of the grid.

    Returns:
    str: A string representing the final state of the grid.
    """
    pattern = ""
    for i in range(1, N + 1):
        row = ""
        for j in range(1, N + 1):
            if i <= j:
                if i % 2 == 0:
                    row += " "
                else:
                    row += "#"
        pattern += row + "\n"
    return pattern

