def xor_of_edges(n, edges):
    """
    Computes the minimum XOR of the labels of the edges on the path from vertex 1 to vertex n
    among all simple paths in a simple connected undirected graph with n vertices and m edges.

    :param n: Number of vertices in the graph
    :param edges: List of tuples (u, v, w) representing edges with labels w
    :return: Minimum XOR of the labels of the edges on the path from vertex 1 to vertex n
    """
    from collections import defaultdict
    graph = defaultdict(list)
    for u, v, w in edges:
        graph[u].append((v, w))
        graph[v].append((u, w))

