def restore_records(S):
    """
    Restores the erased records in the string S by inserting the minimum number of characters.

    Parameters:
    S (str): The original string of i and o.

    Returns:
    int: The minimum number of characters that need to be inserted.
    """
    i_count = S.count('i')
    o_count = S.count('o')
    if i_count % 2 == 0 and o_count % 2 == 0:
        return 0
    elif i_count % 2 == 0:
        return o_count
    elif o_count % 2 == 0:
        return i_count
    else:
