    for i in range(1, N+1):
        dfs(i, 0, [])

    if len(paths) != N:
        return False

    for i in range(1, N+1):
        for j in range(1, K):
            if not (paths[i-1][j] in graph[paths[i-1][j-1]]):
                return False
    return True

