def is_valid_decomposition(N, K, edges):
    """
    Determines if the given tree can be decomposed into N paths each of length K.

    :param N: Number of paths
    :param K: Length of each path
    :param edges: List of edges in the tree
    :return: True if the tree can be decomposed, False otherwise
    """
    # Build the adjacency list representation of the tree
    adj_list = defaultdict(list)
    for u, v in edges:
        adj_list[u].append(v)
        adj_list[v].append(u)

    # Perform a depth-first search to find a valid path decomposition
    def dfs(node, path, path_len, path_count):
        if path_count == N:
            return True
        if path_len == K:
            return dfs(adj_list[node][0], path + [adj_list[node][0]], 0, path_count + 1)
        for neighbor in adj_list[node]:
            if neighbor not in path:
                if dfs(neighbor, path + [neighbor], path_len + 1, path_count):
                    return True
        return False

    # Start DFS from any node
    return dfs(1, [1], 0, 0)

