def read_input():
    """
    Reads the input from standard input and returns the number of rows, columns, grid, starting position, and ending position.
    """
    import sys
    input = sys.stdin.read().split()
    H = int(input[0])
    W = int(input[1])
    grid = [input[i*W:(i+1)*W] for i in range(H)]
    A = int(input[2])
    B = int(input[3])
    C = int(input[4])
    D = int(input[5])
    return H, W, grid, A, B, C, D

def is_within_bounds(grid, row, col):
    """
    Checks if the given row and column are within the bounds of the grid.
    """
    return 0 <= row < len(grid) and 0 <= col < len(grid[0])

def is_road(grid, row, col):
    """
    Checks if the given cell is a road.
    """
    return grid[row][col] == '.'

def is_wall(grid, row, col):
    """
    Checks if the given cell is a wall.
    """
    return grid[row][col] == '#'

def can_move(grid, row, col, direction):
    """
    Checks if the given cell can be moved to in the given direction.
    """
    if direction == 'up':
        return is_within_bounds(grid, row-1, col) and is_road(grid, row-1, col)
    elif direction == 'down':
        return is_within_bounds(grid, row+1, col) and is_road(grid, row+1, col)
    elif direction == 'left':
        return is_within_bounds(grid, row, col-1) and is_road(grid, row, col-1)
    elif direction == 'right':
        return is_within_bounds(grid, row, col+1) and is_road(grid, row, col+1)

def perform_front_kick(grid, row, col, direction):
    """
    Performs a front kick in the given direction and returns the new grid.
    """
    if direction == 'up':
        for i in range(2):
            if is_within_bounds(grid, row-i, col) and is_wall(grid, row-i, col):
                grid[row-i][col] = '.'
    elif direction == 'down':
        for i in range(2):
            if is_within_bounds(grid, row+i, col) and is_wall(grid, row+i, col):
                grid[row+i][col] = '.'
    elif direction == 'left':
        for i in range(2):
            if is_within_bounds(grid, row, col-i) and is_wall(grid, row, col-i):
                grid[row][col-i] = '.'
    elif direction == 'right':
        for i in range(2):
            if is_within_bounds(grid, row, col+i) and is_wall(grid, row, col+i):
                grid[row][col+i] = '.'
    return grid

def find_shortest_path(grid, start_row, start_col, end_row, end_col):
    """
    Finds the shortest path from the start cell to the end cell using BFS.
    """
    from collections import deque
    queue = deque([(start_row, start_col, 0)])
    visited = set()
    visited.add((start_row, start_col))
    while queue:
        row, col, steps = queue.popleft()
        if row == end_row and col == end_col:
            return steps
        for direction in ['up', 'down', 'left', 'right']:
            if can_move(grid, row, col, direction) and (row, col) not in visited:
                new_grid = perform_front_kick(grid.copy(), row, col, direction)
                if (row, col) not in visited:
                    visited.add((row, col))
                    queue.append((row, col, steps + 1))
    return float('inf')

def main():
    """
    Reads the input, finds the shortest path, and prints the number of front kicks needed.
    """
    H, W, grid, start_row, start_col, end_row, end_col = read_input()
    shortest_path = find_shortest_path(grid, start_row, start_col, end_row, end_col)
    print(shortest_path)

if __name__ == "__main__":
    main()
