def is_within_bounds(H, W, x, y):
    """
    Checks if the given coordinates (x, y) are within the bounds of the grid.
    """
    return 1 <= x <= H and 1 <= y <= W

def is_road(grid, x, y):
    """
    Checks if the cell at coordinates (x, y) is a road.
    """
    return grid[x-1][y-1] == '.'

def is_wall(grid, x, y):
    """
    Checks if the cell at coordinates (x, y) is a wall.
    """
    return grid[x-1][y-1] == '#'

def can_kick(grid, x, y, direction):
    """
    Checks if the cell at coordinates (x, y) can be kicked in the given direction.
    """
    if direction == 'up':
        return is_within_bounds(grid, y, x-2) and is_wall(grid, x, y) and is_road(grid, x-1, y)
    elif direction == 'down':
        return is_within_bounds(grid, y, x+2) and is_wall(grid, x, y) and is_road(grid, x+1, y)
    elif direction == 'left':
        return is_within_bounds(grid, x, y-2) and is_wall(grid, x, y) and is_road(grid, x, y-1)
    elif direction == 'right':
        return is_within_bounds(grid, x, y+2) and is_wall(grid, x, y) and is_road(grid, x, y+1)

def perform_kick(grid, x, y, direction):
    """
    Performs a kick in the given direction and returns the new grid.
    """
    if direction == 'up':
        grid[x-2][y-1] = '.'
        grid[x-1][y-1] = '.'
    elif direction == 'down':
        grid[x+2][y-1] = '.'
        grid[x+1][y-1] = '.'
    elif direction == 'left':
        grid[x-1][y-2] = '.'
        grid[x-1][y-1] = '.'
    elif direction == 'right':
        grid[x-1][y+2] = '.'
        grid[x-1][y+1] = '.'
    return grid

def find_min_kicks(grid, start, end):
    """
    Finds the minimum number of kicks needed to move from the start cell to the end cell.
    """
    H, W = len(grid), len(grid[0])
    queue = [(start[0], start[1], 0)]
    visited = set()
    visited.add((start[0], start[1]))
    while queue:
        x, y, kicks = queue.pop(0)
        if (x, y) == end:
            return kicks
        for direction in ['up', 'down', 'left', 'right']:
            if can_kick(grid, x, y, direction):
                new_grid = perform_kick(grid, x, y, direction)
                if (x, y) not in visited:
                    visited.add((x, y))
                    queue.append((x, y, kicks + 1))
    return -1

def main():
    """
    Main function to read input, find the minimum number of kicks, and print the result.
    """
    H, W, grid, A, B, C, D = read_input()
    start = (A, B)
    end = (C, D)
    min_kicks = find_min_kicks(grid, start, end)
    print(min_kicks)

