def solve_problem(N, M, operations):
    """
    Solves the problem of making the sequence x_1 = x_2 = ... = x_N = 1 given a list of operations.

    Parameters:
    N (int): The length of the sequence.
    M (int): The number of operations.
    operations (list of tuples): A list of operations where each operation is a tuple (L_i, R_i).

    Returns:
    None: Prints the minimum cost of operations and the sequence after operations.
    """
    x = [0] * N
    min_cost = float('inf')
    best_sequence = None
    best_operations = []

    for i in range(1 << M):
        current_sequence = x[:]
        current_cost = 0
        for j in range(M):
            if i & (1 << j):
                L, R = operations[j]
                if L <= R:
                    for k in range(L, R + 1):
                        current_sequence[k - 1] = 1
                    current_cost += 1
                else:
                    for k in range(R, L - 1, -1):
                        current_sequence[k - 1] = 1
                    current_cost += 1
        if current_sequence == [1] * N and current_cost < min_cost:
            min_cost = current_cost
            best_sequence = current_sequence[:]
            best_operations = [(L, R) for L, R in operations if i & (1 << (operations.index((L, R))))]

    if best_sequence is None:
        print(-1)
    else:
        print(min_cost)
        for op in best_operations:
            print(op[0], op[1])
