import sys
from collections import deque

def read_input():
    """
    Reads input from stdin and returns the problem specification and the list of changes.
    """
    input_data = sys.stdin.read().strip().split('\n')
    H, W = map(int, input_data[0].split())
    A = [list(map(int, input_data[i + 1].split())) for i in range(H)]
    Q, sh, sw = map(int, input_data[2 * H + 1].split())
    changes = []
    for i in range(2 * H + 2, 2 * H + 2 + Q):
        d, a = input_data[i].split()
        changes.append((d, int(a)))
    return H, W, A, Q, sh, sw, changes

def calculate_f(P, A):
    """
    Calculates the product of all elements in the path P.
    """
    return 1 if not P else A[P[0][0] - 1][P[0][1] - 1] * calculate_f(P[1:], A)

def solve(H, W, A, Q, sh, sw, changes):
    """
    Solves the problem by performing each change and calculating the sum of f(P) over all paths.
    """
    grid = [[A[h - 1][w - 1] for w in range(1, W + 1)] for h in range(1, H + 1)]
    for d, a in changes:
        if d == 'L':
            for h in range(1, H + 1):
                for w in range(1, sw):
                    grid[h][w - 1] = grid[h][w]
            sw -= 1
        elif d == 'R':
            for h in range(1, H + 1):
                for w in range(sw, W + 1):
                    grid[h][w] = grid[h][w - 1]
            sw += 1
        elif d == 'U':
            for h in range(1, sh):
                for w in range(1, W + 1):
                    grid[h - 1][w] = grid[h][w]
            sh -= 1
        elif d == 'D':
            for h in range(sh, H + 1):
                for w in range(1, W + 1):
                    grid[h][w] = grid[h - 1][w]
            sh += 1
        grid[sh][sw] = a
    paths = deque()
    for h in range(1, H + 1):
        for w in range(1, W + 1):
            paths.append([(h, w)])
    for _ in range(Q):
        new_paths = deque()
        while paths:
            path = paths.popleft()
            h, w = path[-1]
            if h == H and w == W:
                new_paths.append(path)
            else:
                if h < H:
                    new_paths.append(path + [(h + 1, w)])
                if w < W:
                    new_paths.append(path + [(h, w + 1)])
        paths = new_paths
    result = 0
    for path in paths:
        result = (result + calculate_f(path, grid)) % 998244353
    return result

