from collections import deque
from functools import reduce
from operator import mul
from sys import stdin, stdout

def read_input():
    """Reads the input from stdin and returns the problem specification and changes."""
    lines = stdin.read().splitlines()
    H, W = map(int, lines[0].split())
    A = [list(map(int, line.split())) for line in lines[1:H+1]]
    Q, sh, sw = map(int, lines[H+1].split())
    changes = []
    for _ in range(Q):
        d, a = lines[H+2+_.H+1].split()
        changes.append((d, int(a)))
    return A, sh, sw, changes

def f(grid, path):
    """Computes the product of all elements in the grid along the given path."""
    return reduce(mul, (grid[h-1][w-1] for h, w in path), 1)

def count_paths(grid, H, W):
    """Counts the number of paths from (1,1) to (H,W) in the grid."""
    MOD = 998244353
    dp = [[0] * (W + 1) for _ in range(H + 1)]
    dp[1][1] = 1
    for h in range(1, H + 1):
        for w in range(1, W + 1):
            if h == 1 and w == 1:
                continue
            if h > 1:
                dp[h][w] = (dp[h][w] + dp[h-1][w]) % MOD
            if w > 1:
                dp[h][w] = (dp[h][w] + dp[h][w-1]) % MOD
    return dp[H][W]

def main():
    """Reads the input, computes the answer, and writes it to stdout."""
    A, sh, sw, changes = read_input()
    H, W = len(A), len(A[0])
    grid = [[0] * W for _ in range(H)]
    grid[sh-1][sw-1] = A[sh-1][sw-1]
    for d, a in changes:
